if (msg && msg.payload) {
    msg.payload = {
        "tab": "Home-" + msg.payload.account
    };
    return msg;
}

// Save users-array from firebase for future use
flow.set('users', msg.payload);

return msg;

// Get users-array from firebase
var users = flow.get('users');

// Get username and password from user
var username = msg.payload.username;
var password = msg.payload.password;

// Login status
var status = 'failed';

// Check username and password
for (let index = 0; index < users.length; index++) {
    const user = users[index];

    // If username match
    if (user.username == username) {
        // If password match
        if (user.password == password) {
            status = 'successful';
        }
        // Then, no need to check anymore
        break;
    }
}

msg.payload = {
    "account": username,
    "status": status
}
return msg;

if (msg.payload.status == "failed") {
    msg.payload = "Invalid login, please try again!";
    return msg;
}

msg.payload = "[Welcome to NaVi] Log in with your NaVi account to continue";
