var currentTime = new Date().getTime();
var startTime = flow.get("startTime");

//                          <UNIX time 0>
// Date(milisecond) -> (1970/01/01) 0h:0m:0s + milisecond
//                  -> convert to zone
msg.payload = new Date(currentTime - startTime);
return msg;

