msg.alertMsg = "[Cảnh báo!] Độ ẩm trong không khí hiện tại là: "
    + String(Math.round(msg.payload)) + "%, ";
if (msg.alert_high_humidity == true) {
    msg.payload = msg.alertMsg + "cao hơn mức an toàn!";
    return msg;
} else if (msg.alert_low_humidity == true) {
    msg.payload = msg.alertMsg + "thấp hơn mức an toàn!";
    return msg;
}

msg.alertMsg = "[Cảnh báo!] Nhiệt độ môi trường hiện tại là: "
