if (msg.alert_high_temperature == true) {
    msg.payload = msg.alertMsg + "cao hơn mức an toàn!";
    return msg;
} else if (msg.alert_low_temperature == true) {
    msg.payload = msg.alertMsg + "thấp hơn mức an toàn!";
