if (msg.alert_high_smoke == true) {
    msg.payload = msg.alertMsg + "cao hơn mức an toàn!";
    return msg;
}

// Khi robot vừa khởi động 
if (msg.topic == "ready") {
    msg.payload = "[Thông báo] Robot đã sẵn sàng hoạt động.";

    // audio out
    msg.audio = "The robot is ready to go.";
    return msg;
}

//msg.info = "[Thông báo] Robot đã chuyển sang chế độ: ";
msg.info = "[Thông báo] Robot đang ở chế độ: ";

if (msg.payload == "auto") {
    msg.payload = msg.info + "tự động dò đường.";
} else if (msg.payload == "manual") {
    msg.payload = msg.info + "điều khiển thông qua trang web.";
} else if (msg.payload == "stop") {
    msg.payload = msg.info + "dừng di chuyển.";
}
// audio out
msg.audio = "The robot has switched mode.";
return msg;

if (msg.payload == "detected") {
    msg.payload = "[Phát hiện!] Đã tìm thấy đối tượng!";
    // audio out
    msg.audio = "Found!";
    return msg;
}

if (msg.audio == null) {
    // msg = {
