/* Thêm gói dữ liệu mới gồm {topic và payload} 
vào mảng dashboardLog và lưu trữ lại trong miền flow 
*/
// init counter to 0 if it does NOT exist already
var dashboardLog = flow.get('dashboardLog') || [];

dashboardLog.push(msg);
if (dashboardLog.length > 20) {
    // delete oldest msg if > 20
    dashboardLog.shift();
    dashboardLog.length = 20;
}
// console.log(dashboardLog.length);

// store the value back
flow.set('dashboardLog', dashboardLog);

// make it part of the outgoing msg object
msg = {};
msg.payload = dashboardLog; 
return msg;

